mapper.select:
select acid, uniacid, userid, orderStatus, contactName, contactMobile, mileage, carTypeName,
carNumber, appointmentTime, cancelType, createTime, confirmTime, finishTime, cancelAdminUser,
confirmAdminUser, finishAdminUser, updateTime
from lf_4s_appointment_order
where userid = #{userId}

    and orderStatus = #{orderStatus}


    and createTime >= #{startTime}


    and createTime <= #{endTime}

select acid, uniacid, userid, orderStatus, contactName, contactMobile, mileage, carTypeName,
carNumber, appointmentTime, cancelType, createTime, confirmTime, finishTime, cancelAdminUser,
confirmAdminUser, finishAdminUser, updateTime
from lf_4s_appointment_order
where uniacid = #{uniacid}

    and orderStatus = #{orderStatus}


    and createTime >= #{startTime}


    and createTime <= #{endTime}
